(function(){var prefersDark=window.matchMedia('(prefers-color-scheme: dark)'),htmlNode=document.documentElement,currentTheme=null;function _setThemeImpl(actualTarget){try{if(actualTarget!==null){htmlNode.className=htmlNode.className.replace(/ (theme|view)-[^\s]+/ig,'');htmlNode.classList.add('theme-'+actualTarget,'view-'+{"light":"light","dark":"dark"}[actualTarget]);}}catch(ex){setTimeout(function(){throw ex;},0);}}function _setFromPCS(){_setThemeImpl(prefersDark.matches?'dark':'light');}window.MwSkinTheme=Object.freeze({LOCAL_THEME_PREFERENCE_KEY:'skin-theme',LOCAL_FEATURE_PREFERENCE_KEY:'skin-theme-features',getCurrent:function(){return currentTheme;},set:function(target){currentTheme=target;if(currentTheme==='auto'){_setFromPCS();htmlNode.classList.add('theme-auto');prefersDark.addEventListener('change',_setFromPCS);}else{_setThemeImpl(currentTheme);prefersDark.removeEventListener('change',_setFromPCS);}},});MwSkinTheme.set(localStorage.getItem(MwSkinTheme.LOCAL_THEME_PREFERENCE_KEY)||RLCONF.wgCurrentTheme||"light");}());